export const defaultIcons = {
  default: Pin,
  bookmark: Bookmark,
  warning: Warning,
  star: Star,
};

export const isDefaultIcon = (icon: string): boolean =>
  Boolean(defaultIcons[icon]);

export const SlotIcon: React.FunctionComponent<{
  icon: string;
  color?: string;
}> = ({ color, icon }) => {
  switch (icon) {
    case "default":
    case "warning":
    case "star":
    case "bookmark": {
      const Icon = defaultIcons[icon];
      return <Icon color={color} />;
    }
  }

  return <img alt="pinned message" src={icon} />;
};
